let ldr1 = parseFloat(msg.payload["LDR1"]);
let ldr2 = parseFloat(msg.payload["LDR2"]);
let maxLight;
let highLDR;

if (!isNaN(ldr1) && !isNaN(ldr2)) {
    if (ldr1 > ldr2) {
        maxLight = ldr1.toFixed(2);
        highLDR =  'LDR 1';    
    } else {
        maxLight = ldr2.toFixed(2);
        highLDR =  'LDR 2'; 
    }
    global.set("maxLight",maxLight);
    global.set("highLDR",highLDR);
     return { payload: maxLight, high: highLDR };
} else {
    return { payload: -1,high: 'No Data!'  };
}

let minAngle = parseFloat(flow.get("minAngle"));
let controlFactor = parseFloat(flow.get("controlFactor"));
let maxLight = parseFloat(global.get("maxLight"));
let higherLDR = global.get("highLDR");
let constD;
let angle;

if (higherLDR === "LDR 1"){
        constD = 1.5;
    }
else{
        constD = 0.5;
    }
angle = Math.round(Math.min(minAngle*constD+(180-minAngle)*maxLight*controlFactor,180));
return {payload:angle};
// return {a:minAngle,b:controlFactor,c:medicineType,d:maxLight,e:higherLDR,f:angle,g:constD}

flow.set("medicineType",msg.payload);
let defMedData = flow.get("defMedConfig");
let minAngle;
let controlFactor;

if (msg.payload ===2){
    minAngle = defMedData["A"]["minAngle"];
    controlFactor =  defMedData["A"]["controlFactor"];
}
else if (msg.payload ===3){
    minAngle =  defMedData["B"]["minAngle"];
    controlFactor = defMedData["B"]["controlFactor"];
}
else{
    minAngle = defMedData["C"]["minAngle"];
    controlFactor = defMedData["C"]["controlFactor"];
}
return {payload:{"minAngle": minAngle, "controlFactor": controlFactor}};

